UPDATE CONTRACT
SET NAME = ? , PHONE = ?, EMAIL = ?, ADDRESS = ?, BIRTH = ?
WHERE NAME = ? ;

UPDATE CONTRACT_GROUP
SET GROUP_NAME = ?
WHERE GROUP_NAME = ? ;

UPDATE CONTRACT
SET GROUP_NO = (SELECT GROUP_NO FROM CONTRACT_GROUP WHERE GROUP_NAME = ?)
WHERE NAME = ?;

UPDATE CONTRACT
SET GROUP_NO = null
WHERE NAME = ?;
